      type: "List",
      heading: "ソンドン洞窟探検",
      items: [
        "4日間・レベル6",
        "プロモーション2020： 58,200,000VND",
        "通常価格 69,800,000VND　/（〜$3,000）",
      ],
    },
  ],
};

export default SondoongJSON;
